function spanleftbar(){document.getElementById("numbers").style.height="100px",document.getElementById("numbers").style.height=document.getElementById("x2").scrollHeight+document.body.scrollHeight+"px"}function fill(){document.getElementById("numbers").innerHTML="";for(var e=1;e<500;e++)document.getElementById("numbers").innerHTML+=e+"<br>"}window.onload=(()=>{spanleftbar(),fill()}),window.onresize=spanleftbar;